import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LanguageSelection from './sellang.js';
import LoginPage from './login.js';
import { ThemeProvider as StyledThemeProvider } from 'styled-components'; // تغییر نام
import { ThemeProvider as AppThemeProvider, useTheme } from './themecontext';

function App() {
  const themeContext = useTheme();

  if (!themeContext) {
    return <div>Loading Theme...</div>;
  }

  return (
    <Router>
      <AppThemeProvider>
        <StyledThemeProvider theme={themeContext.theme}> {/* پاس دادن تم به StyledThemeProvider */}
          <Routes>
            <Route path="/" element={<LanguageSelection />} />
            <Route path="/login" element={<LoginPage />} />
            ... سایر روت‌ها
          </Routes>
        </StyledThemeProvider>
      </AppThemeProvider>
    </Router>
  );
}

export default App;
