import React, { createContext, useState, useContext } from 'react';
import ReactCountryFlag from 'react-country-flag';

export const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleTheme = () => {
        setIsDarkMode(!isDarkMode);
    };

    const lightTheme = {
        background: '#f8f8f8',
        text: '#333',
        buttonBackground: '#fff',
        buttonHover: '#e0e0e0',
        buttonText: '#333',
        buttonHoverText: '#666',
        inputBackground: '#fff',
        border: '#ccc',
        // background: '#f4f4f4',
        // text: '#333',
        primary: '#007bff',
        primaryHover: '#0056b3',
        secondaryText: '#6c757d',
        // buttonText: '#fff',
        cardBackground: '#fff',
        // inputBackground: '#e9ecef',
        // border: '#ced4da',
        heading: '#212529',
    };

    const darkTheme = {
        background: '#121212',
        text: '#eee',
        buttonBackground: '#333',
        buttonHover: '#555',
        buttonText: '#eee',
        buttonHoverText: '#ccc',
        inputBackground: '#333',
        border: '#555',
        // background: '#121212',
        // text: '#f8f9fa',
        primary: '#5bc0de',
        primaryHover: '#31b0d5',
        secondaryText: '#adb5bd',
        // buttonText: '#fff',
        cardBackground: '#212529',
        // inputBackground: '#343a40',
        // border: '#495057',
        heading: '#fff',
        // Add any other properties you need for dark mode      
    };

    const theme = isDarkMode ? darkTheme : lightTheme;

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    );
};

export const useTheme = () => useContext(ThemeContext);
