import React, { useEffect, useState } from 'react'; // ایمپورت useState
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LanguageSelection from './sellang.js';
import LoginPage from './login.js';
import { ThemeProvider, useTheme } from './themecontext.js';
import { useTranslation } from 'react-i18next';

function App() {
  const themeContext = useTheme();
  const { i18n } = useTranslation();
  const [isThemeLoaded, setIsThemeLoaded] = useState(!!themeContext); // State برای وضعیت لود شدن تم
  const { currentLanguage } = themeContext || {}; // مقداردهی اولیه با یک شیء خالی برای جلوگیری از خطای دِستراکچر در ابتدا

  useEffect(() => {
    if (themeContext) {
      setIsThemeLoaded(true);
    }
  }, [themeContext]);

  useEffect(() => {
    if (isThemeLoaded && currentLanguage && currentLanguage !== i18n.language) {
      i18n.changeLanguage(currentLanguage);
    }
  }, [isThemeLoaded, currentLanguage, i18n]);

  if (!isThemeLoaded) {
    return <div>Loading Theme...</div>; // یا یک کامپوننت لودینگ مناسب
  }

  return (
    <Router>
      <ThemeProvider>
        <Routes>
          <Route path="/" element={<LanguageSelection />} />
          <Route path="/login" element={<LoginPage />} />
          {/* ... سایر روت‌ها */}
        </Routes>
      </ThemeProvider>
    </Router>
  );
}

export default App;
